-- ----------------------------
-- Table structure for event_result
-- ----------------------------
DROP TABLE IF EXISTS `event_result`;
CREATE TABLE `event_result`  (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `event_id` int(11) NULL DEFAULT NULL COMMENT '赛事ID',
  `user_id` int(11) NULL DEFAULT NULL COMMENT '用户ID',
  `award_id` int(11) NULL DEFAULT NULL COMMENT '奖项ID',
  `host_id` int(11) NULL DEFAULT NULL COMMENT '主办方ID',
  `date` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '颁奖日期',
  `address` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '领奖地址',
  `phone` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '联系方式',
  `status` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '状态',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 11 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci COMMENT = '赛事结果' ROW_FORMAT = Dynamic;

