-- ----------------------------
-- Table structure for news
-- ----------------------------
DROP TABLE IF EXISTS `news`;
CREATE TABLE `news`  (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `title` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '标题',
  `description` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '简介',
  `img` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '图片',
  `content` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL COMMENT '内容',
  `read_count` int(11) NULL DEFAULT NULL COMMENT '阅读量',
  `host_id` int(11) NULL DEFAULT NULL COMMENT '主办方ID',
  `status` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '审核状态',
  `reason` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '审核理由',
  `time` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '发布时间',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 12 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci COMMENT = '赛事资讯' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of news
-- ----------------------------
INSERT INTO `news` VALUES (4, '在编程竞赛的代码世界中突破自我', '在数字化浪潮席卷全球的时代，编程竞赛如同一场代码与智慧的盛宴，吸引着无数技术爱好者投身其中。它不仅是对编程技能的考验，更是一场思维与创造力的激烈角逐。从算法设计到代码实现，从逻辑优化到系统调试，编程竞赛以独特的魅力，为参赛者搭建起探索技术边界、挑战自我极限的舞台。', 'http://localhost:9090/files/download/1745973892902-资讯8.png', '<p>在数字化浪潮席卷全球的时代，编程竞赛如同一场代码与智慧的盛宴，吸引着无数技术爱好者投身其中。它不仅是对编程技能的考验，更是一场思维与创造力的激烈角逐。从算法设计到代码实现，从逻辑优化到系统调试，编程竞赛以独特的魅力，为参赛者搭建起探索技术边界、挑战自我极限的舞台。​</p><p>编程竞赛的价值远超奖项本身。在竞赛中，参赛者需要将所学的编程语言、数据结构与算法知识灵活运用，解决实际问题。这一过程不仅能加深对编程原理的理解，还能培养高效的问题解决能力和创新思维。例如，在 ACM 国际大学生程序设计竞赛中，面对复杂的算法题，选手需快速分析问题本质，设计出高效的算法，并在有限时间内完成代码编写与调试。这种高强度的训练，能显著提升编程速度、代码质量和逻辑思维能力，而这些能力正是未来从事软件开发、人工智能等领域工作的核心竞争力。此外，编程竞赛还能让参赛者接触到行业前沿的技术和理念，拓宽视野，为职业发展奠定坚实基础。​</p><p>备战编程竞赛是一个系统且充满挑战的过程。首先，夯实编程基础是关键。参赛者需要熟练掌握 C++、Python 等主流编程语言，深入理解数据结构与算法原理，如链表、树、图的构建与操作，动态规划、贪心算法等经典算法的应用。通过大量练习 LeetCode、洛谷等平台的题目，积累解题经验，提升编程熟练度。同时，组建一支默契的竞赛团队也至关重要。在团队中，成员分工明确，有人负责算法设计，有人专注代码实现，有人进行测试与调试。团队定期开展模拟训练，按照竞赛规则和时间要求进行实战演练，磨合协作流程，提升整体作战能力。此外，关注技术论坛和开源项目，学习优秀代码案例，了解最新算法优化思路，也是提升竞赛水平的有效途径。​</p><p>编程竞赛的赛场上，分秒必争，充满挑战。当竞赛题目发布后，团队需迅速分工协作，分析题目要求，确定解题思路。有些题目看似简单，却可能隐藏着边界条件和性能陷阱；有些题目难度较大，需要团队成员集思广益，突破思维定式。在编写代码过程中，不仅要保证程序逻辑正确，还要注重代码的可读性和运行效率。面对程序运行错误或超时等问题，选手需具备快速定位和解决问题的能力，通过调试工具和日志分析，逐步排查错误根源。竞赛后期，团队还需对提交的代码进行反复测试，确保在各种测试用例下都能稳定运行，不放过任何一个细节。​</p><p>参与编程竞赛的收获是多维度的。竞赛过程中，与队友并肩作战、攻克难题的经历，培养了深厚的团队协作精神和沟通能力；在解决复杂问题时，不断尝试、失败、再尝试的过程，锤炼了坚韧不拔的意志和抗压能力。即使未能取得理想成绩，在备赛和竞赛过程中积累的知识、经验和技术能力，也将成为未来学习和工作的宝贵财富。而对于表现优异的选手来说，编程竞赛的经历不仅是个人能力的有力证明，还能获得更多的实习、升学机会，为职业生涯开辟更广阔的道路。</p>', 0, 1, '通过', NULL, '2025-04-29 11:00:00');
INSERT INTO `news` VALUES (5, '在数学竞赛的星空中追寻智慧之光', '数学，作为科学的基石，以其严谨的逻辑、精妙的思维和无穷的魅力，构筑起一座令人神往的知识殿堂。而数学竞赛，则像是这座殿堂中璀璨的星辰，吸引着无数热爱思考、勇于挑战的学子投身其中，在解题的过程中感受思维碰撞的火花，追寻智慧的光芒。​', 'http://localhost:9090/files/download/1745973888672-资讯7.png', '<p>数学，作为科学的基石，以其严谨的逻辑、精妙的思维和无穷的魅力，构筑起一座令人神往的知识殿堂。而数学竞赛，则像是这座殿堂中璀璨的星辰，吸引着无数热爱思考、勇于挑战的学子投身其中，在解题的过程中感受思维碰撞的火花，追寻智慧的光芒。​</p><p>数学竞赛的意义远不止于一纸奖状。它是对参赛者数学思维深度与广度的全方位考验。在竞赛的舞台上，传统课堂中的定理与公式不再是简单的记忆对象，而是需要灵活运用、创新组合的 “武器”。通过解决竞赛中复杂多变的题目，参赛者能够锻炼逻辑推理能力、空间想象能力和数学建模能力。例如，在几何证明题中，需要从复杂的图形中抽丝剥茧，找到隐藏的逻辑关系；在数论问题里，要深入探索数字背后的规律。这种思维训练，不仅有助于提升数学成绩，更能培养严谨的思维习惯和面对困难时的坚韧品格，这些能力在未来的学习和工作中都将发挥重要作用。​</p><p>备战数学竞赛是一个系统且充满挑战的过程。首先，扎实的基础知识是一切的前提。参赛者需要将课本中的概念、定理理解透彻，构建起完整的知识体系。在此基础上，深入钻研竞赛教材，接触各种经典题型和解题方法。许多优秀的数学竞赛选手都会建立自己的错题本，将易错、典型的题目整理记录，分析错误原因和解题思路，通过反复练习强化记忆。同时，参加专业的培训课程和线上学习社区也是提升能力的有效途径。在培训中，能够得到经验丰富的老师指导，掌握高效的解题技巧；在学习社区里，可以与其他参赛者交流心得，拓宽解题思路。此外，定期进行模拟训练，严格按照竞赛时间要求答题，有助于提升解题速度和考试时的心理素质。​</p><p>数学竞赛的赛场上，每一分钟都充满着紧张与挑战。当拿到试卷的那一刻，参赛者需要迅速调整状态，冷静分析题目。有些题目看似简单，实则暗藏玄机；有些题目初看无从下手，却能在深入思考后找到突破口。在解题过程中，不仅需要强大的知识储备，更要保持清晰的思维和稳定的心态。面对难题时，不能急躁，要尝试从不同角度思考，运用多种方法求解；遇到熟悉的题目，也不能掉以轻心，避免因疏忽大意而失分。竞赛结束后，无论结果如何，对题目和解题过程的复盘总结都至关重要。通过与他人交流解法，能够发现自己的不足，学习到更巧妙的思路和方法。​</p><p>参与数学竞赛的收获是多维度的。那些在备赛过程中付出的汗水，在赛场上经历的紧张与兴奋，以及与志同道合的伙伴共同探讨问题的时光，都成为了宝贵的人生记忆。即使未能取得理想的成绩，在这个过程中培养的思维能力、学习方法和面对挑战的勇气，也将成为一生的财富。而对于成绩优异的选手来说，数学竞赛不仅是对自身能力的认可，更能为未来的学术道路和职业发展奠定坚实的基础，打开一扇通往更高平台的大门。​</p><p>数学竞赛如同一场永无止境的探索之旅，它引领着我们在数学的星空中不断追寻智慧之光。在这个过程中，我们收获的不仅是知识与荣誉，更是思维的淬炼和心灵的成长。期待更多的学子能踏上这场奇妙的旅程，在数学竞赛的舞台上绽放属于自己的光彩。</p>', 0, 1, '通过', NULL, '2025-04-29 11:00:00');
INSERT INTO `news` VALUES (6, '探索知识边界的奇妙之旅', '在知识迭代加速的时代，学科竞赛已成为突破学术壁垒、激发创新活力的前沿阵地。它不再局限于单一学科的比拼，而是演变为跨领域知识融合、实践能力与创新思维碰撞的舞台。从人工智能竞赛中探索算法的无限可能，到人文社科竞赛里挖掘社会现象的深层逻辑，学科竞赛正以多元化的形式，引领参赛者突破固有认知，在探索未知的过程中，感受知识与智慧交织的独特魅力。​', 'http://localhost:9090/files/download/1745973882512-资讯6.png', '<p>在知识迭代加速的时代，学科竞赛已成为突破学术壁垒、激发创新活力的前沿阵地。它不再局限于单一学科的比拼，而是演变为跨领域知识融合、实践能力与创新思维碰撞的舞台。从人工智能竞赛中探索算法的无限可能，到人文社科竞赛里挖掘社会现象的深层逻辑，学科竞赛正以多元化的形式，引领参赛者突破固有认知，在探索未知的过程中，感受知识与智慧交织的独特魅力。​</p><p>想要在学科竞赛中崭露头角，需以敏锐的洞察力把握竞赛趋势。随着科技发展，许多竞赛开始注重对数字化工具的运用和跨学科能力的考查。参赛者应紧跟时代步伐，主动学习 Python 数据分析、三维建模等新兴技能，拓宽知识领域。同时，深入研究竞赛规则与评分标准，挖掘其中的关键信息，明确评审重点，以此为导向制定个性化备赛方案，让努力更具针对性和实效性。​</p><p>高效的学习策略是备赛的核心武器。在信息爆炸的当下，参赛者可利用慕课平台、学术数据库等资源，系统学习专业知识。采用思维导图梳理知识脉络，建立完整的知识体系；通过案例分析、小组研讨等方式，加深对知识点的理解与运用。此外，定期进行复盘总结，分析模拟练习中的错题和薄弱环节，及时调整学习方法，实现学习效率的最大化提升。​</p><p>学科竞赛的魅力，不仅在于个人能力的提升，更在于与优秀者同行的成长体验。赛场上，参赛者有机会与来自不同地区、不同背景的高手交流切磋，在观点交锋中拓宽视野，在合作竞争中汲取经验。这种跨地域、跨学科的思维碰撞，往往能打破思维定式，激发出创新灵感。同时，竞赛过程中结识的良师益友，也将成为未来学术或职业道路上的宝贵人脉资源。</p>', 1, 1, '通过', NULL, '2025-04-29 11:00:00');
INSERT INTO `news` VALUES (7, '培养知识的灵活运用和创新思维', '学科竞赛就像一场知识的盛宴，它以独特的魅力吸引着无数怀揣梦想的学子投身其中。不同于传统考试，竞赛更强调对知识的灵活运用、创新思维的激发以及解决实际问题的能力。无论是数学建模竞赛中对复杂数据的剖析整合，还是科技创新大赛里对前沿技术的探索实践，学科竞赛都为学生提供了一个展现智慧与才能的平台，让他们在挑战中突破自我，实现知识与能力的双重进阶。​', 'http://localhost:9090/files/download/1745973876893-资讯5.png', '<p>学科竞赛就像一场知识的盛宴，它以独特的魅力吸引着无数怀揣梦想的学子投身其中。不同于传统考试，竞赛更强调对知识的灵活运用、创新思维的激发以及解决实际问题的能力。无论是数学建模竞赛中对复杂数据的剖析整合，还是科技创新大赛里对前沿技术的探索实践，学科竞赛都为学生提供了一个展现智慧与才能的平台，让他们在挑战中突破自我，实现知识与能力的双重进阶。​</p><p>成功参与学科竞赛，周密的备赛计划不可或缺。首先，要依据竞赛的具体要求和自身实际情况，制定清晰的学习目标与时间规划。比如，为编程竞赛预留充足的算法学习与代码练习时间，针对学科知识竞赛系统梳理核心知识点。同时，广泛收集竞赛相关资料，包括往届优秀作品、专家讲座视频等，从中汲取经验，明确努力方向。此外，积极参加校内组织的培训和模拟赛，通过实战演练提前适应竞赛节奏，发现并弥补自身不足。​</p><p>团队协作在学科竞赛中起着至关重要的作用。一个优秀的竞赛团队，成员间需要有明确的分工和高效的沟通。以创新创业类竞赛为例，有人负责市场调研与方案策划，有人专注技术研发，有人擅长展示汇报，各展所长、协同作战。在备赛过程中，难免会遇到意见分歧，但通过充分的讨论与交流，往往能碰撞出更具创意的火花，让项目不断优化完善。团队成员间的相互支持与鼓励，更是克服困难、坚持到最后的强大动力。​</p><p>面对学科竞赛，保持良好的心态同样关键。竞赛的竞争激烈，结果充满不确定性，在备赛过程中可能会遇到重重困难，甚至遭遇失败的打击。这时，要学会调整心态，将竞赛视为提升自我的过程，而非单纯追求名次。遇到难题时，不气馁、不焦虑，冷静分析问题根源；取得阶段性成果时，不骄傲、不自满，保持继续前进的动力。以平和的心态对待竞赛，才能发挥出最佳水平，收获比成绩更珍贵的成长。​</p><p>学科竞赛不仅是一场知识与能力的较量，更是一段宝贵的人生经历。它锻炼了我们的思维能力、实践能力和抗压能力，培养了团队精神和创新意识。这些收获，将成为我们未来学习和工作中的宝贵财富，助力我们在人生道路上走得更远、更稳。期待更多学子能勇敢地投身学科竞赛的浪潮，在拼搏与奋斗中，驶向成长的彼岸，书写属于自己的精彩篇章。</p>', 1, 1, '通过', NULL, '2025-04-29 11:00:00');
INSERT INTO `news` VALUES (8, '学科竞赛：在挑战中挖掘自身潜力', '学科竞赛是学术探索与能力提升的重要平台，它打破了课堂的边界，为学子们搭建起一个将理论知识转化为实践成果的广阔舞台。无论是数理化的思维碰撞，还是文史哲的思想交锋，每一场竞赛都是对专业知识深度与广度的检验。参与其中，不仅能巩固所学，更能接触到前沿学术动态，激发对学科的深层热爱，在挑战中挖掘自身潜力。\n', 'http://localhost:9090/files/download/1745973871309-资讯4.png', '<p>学科竞赛是学术探索与能力提升的重要平台，它打破了课堂的边界，为学子们搭建起一个将理论知识转化为实践成果的广阔舞台。无论是数理化的思维碰撞，还是文史哲的思想交锋，每一场竞赛都是对专业知识深度与广度的检验。参与其中，不仅能巩固所学，更能接触到前沿学术动态，激发对学科的深层热爱，在挑战中挖掘自身潜力。<br></p><p>充分的准备是竞赛成功的基石。首先，参赛者需全面梳理学科知识体系，查缺补漏，构建扎实的知识框架。同时，针对竞赛规则与考核重点，进行专项训练，如通过分析历年真题总结命题规律，模拟实战提升解题速度与准确率。此外，组建团队协作备赛也是关键，成员间优势互补、思维碰撞，能有效提升整体竞争力，为竞赛做好全方位准备。<br></p><p>竞赛过程本身就是一场珍贵的成长之旅。从赛场上的紧张作答，到团队成员间的默契配合，每一个环节都充满挑战与机遇。面对难题时的冷静思考，突发状况下的灵活应对，不仅考验着参赛者的知识储备，更磨砺着心理素质与应变能力。这个过程中，无论是成功的喜悦，还是失败的教训，都将成为个人成长路上的宝贵财富，塑造更加坚韧自信的自己。<br></p><p>学科竞赛的成果往往具有深远影响。获得优异成绩，不仅是对个人能力的高度认可，也能为升学、求职增添亮眼的履历。即使未能获奖，在备赛与参赛过程中积累的知识、经验，以及培养的思维能力和团队协作精神，都将在未来的学习和工作中持续发挥作用。竞赛经历还能拓宽人脉，结识志同道合的伙伴与优秀导师，为未来的学术或职业发展创造更多可能性。<br></p><p>学科竞赛是一场充满挑战与机遇的学术征程。它既是对知识的检验，也是自我突破的契机。无论结果如何，每一次参与都是一次成长，每一份努力都不会被辜负。期待更多学子勇敢踏上竞赛舞台，在探索中收获知识，在竞争中超越自我，书写属于自己的精彩篇章。</p>', 10, 1, '待审核', NULL, '2025-04-29 11:00:00');
INSERT INTO `news` VALUES (9, '数学建模竞赛即将启动', '为激发学生对数学学科的探索热情，培养运用数学知识解决实际问题的能力，“智慧杯” 数学建模竞赛在多方期待中正式启动。早在比赛筹备阶段，主办方就通过线上线下多渠道开展宣传动员。线上，利用微信公众号、微博、抖音等平台发布竞赛预告、往届精彩回顾及丰厚奖励机制；线下，在各高校张贴海报、举办宣讲会，详细介绍竞赛规则与意义，最终吸引了全省 20 余所高校的 300 余支队伍参赛。本次竞赛由省数学学会主办，数学科学学院承办。​', 'http://localhost:9090/files/download/1745939322888-资讯1.jpg', '<p>为激发学生对数学学科的探索热情，培养运用数学知识解决实际问题的能力，“智慧杯” 数学建模竞赛在多方期待中正式启动。早在比赛筹备阶段，主办方就通过线上线下多渠道开展宣传动员。线上，利用微信公众号、微博、抖音等平台发布竞赛预告、往届精彩回顾及丰厚奖励机制；线下，在各高校张贴海报、举办宣讲会，详细介绍竞赛规则与意义，最终吸引了全省 20 余所高校的 300 余支队伍参赛。本次竞赛由省数学学会主办，数学科学学院承办。​</p><p>为保障竞赛的专业性与公平性，主办方邀请了多位国内知名数学专家与企业数据分析师组成命题组与评审团，围绕 “新能源汽车电池寿命预测”“城市交通拥堵优化方案” 等前沿课题设计赛题。赛前，承办方通过线上线下相结合的方式，开展了为期两周的赛前培训，涵盖数学模型构建、编程算法应用等核心技能。同时，还搭建了专属竞赛平台，提供实时技术支持与答疑服务，确保参赛队伍能够全身心投入比赛。​</p><p>竞赛过程中，参赛队伍争分夺秒，从数据收集、模型建立到编程求解，每个环节都展现出扎实的专业功底与创新思维。实验室里，键盘敲击声与讨论声交织，队员们时而眉头紧锁思索难题，时而因灵感迸发而面露欣喜。经过 72 小时的激烈角逐，各支队伍提交的方案不仅展现出数学理论与实际应用的深度融合，还提出了许多具有前瞻性的解决方案。​</p><p>此次竞赛不仅为数学学科领域挖掘和培养了优秀人才，也进一步推动了高校间的学术交流，激发了学生在数学建模领域的研究热情。赛后，部分优秀方案还将被推荐至相关企业，助力产学研深度融合，为未来相关领域的技术突破与创新发展奠定了坚实基础。</p>', 10, 2, '通过', NULL, '2025-04-29 23:10:02');
INSERT INTO `news` VALUES (10, '“未来科技之星” 创新大赛', '为响应国家创新驱动发展战略，鼓励青年学子投身科技创新实践，由市科学技术协会、市教育局联合主办的 “未来科技之星” 创新大赛，于 1 月 15 日在市科技馆隆重开赛。大赛自启动以来，便受到广泛关注，来自全市 35 所高校、科研机构的 120 余个项目报名参赛，涵盖人工智能、新能源、生物科技等多个前沿领域。​', 'http://localhost:9090/files/download/1745974083903-资讯13.png', '<p>为响应国家创新驱动发展战略，鼓励青年学子投身科技创新实践，由市科学技术协会、市教育局联合主办的 “未来科技之星” 创新大赛，于 1 月 15 日在市科技馆隆重开赛。大赛自启动以来，便受到广泛关注，来自全市 35 所高校、科研机构的 120 余个项目报名参赛，涵盖人工智能、新能源、生物科技等多个前沿领域。​</p><p>为保障赛事顺利进行，主办方在赛前做足准备。不仅邀请了行业专家、企业高管组成专业评审团，还为参赛团队组织了创新思维培训、项目路演技巧讲座等活动。同时，搭建了线上线下双赛道，线上平台支持项目资料提交与实时沟通，线下展区则为实物展示与现场答辩提供了专业场地，确保各参赛项目都能充分展示风采。​</p><p>比赛现场气氛热烈，各参赛团队纷纷拿出 “看家本领”。在人工智能展区，一款基于深度学习的智能垃圾分类机器人吸引众人目光，它能快速识别不同类型垃圾并精准分类；新能源展区内，学生研发的新型太阳能储能装置，凭借高效转化与稳定输出性能，赢得专家频频点头。每个项目的路演答辩环节，选手们自信满满，用清晰的逻辑和生动的演示，向评委与观众展示项目的创新性与实用性。​</p><p>此次 “未来科技之星” 创新大赛不仅是一场科技成果的展示盛宴，更是发掘创新人才、孵化优质项目的重要平台。赛后，部分优秀项目将获得创业孵化支持与企业投资对接机会，推动科研成果向实际生产力转化。大赛的成功举办，也将进一步激发青年学子的创新活力，为区域科技发展注入新动能。</p>', 4, 2, '待审核', NULL, '2025-04-29 23:10:36');
INSERT INTO `news` VALUES (11, '人工智能知识竞赛', '为推动人工智能教育普及，培养青少年的创新实践能力和团队协作精神，由市教育局主办的 “智创未来” 机器人创意比赛于 3 月 10 日在市青少年活动中心盛大启幕。本次比赛以 “科技赋能生活” 为主题，面向全市中小学生，吸引了来自 80 余所学校的 200 余支队伍报名参赛，参赛作品涵盖智能家居、环保清洁、医疗辅助等多个应用场景。​', 'http://localhost:9090/files/download/1745974035061-资讯11.png', '<p>为推动人工智能教育普及，培养青少年的创新实践能力和团队协作精神，由市教育局主办的 “智创未来” 机器人创意比赛于 3 月 10 日在市青少年活动中心盛大启幕。本次比赛以 “科技赋能生活” 为主题，面向全市中小学生，吸引了来自 80 余所学校的 200 余支队伍报名参赛，参赛作品涵盖智能家居、环保清洁、医疗辅助等多个应用场景。​</p><p>赛前，主办方联合专业科技教育机构，开展了为期一个月的机器人编程与机械搭建培训课程，帮助参赛学生掌握基础技术。同时，组建了由高校机器人领域专家、企业工程师组成的顾问团队，为参赛队伍提供技术指导和创意优化建议。比赛现场还设置了设备调试区和技术支持站，确保各参赛队伍能够顺利完成作品展示。​</p><p>随着比赛正式开始，现场瞬间成为科技创意的舞台。小学组的 “智能浇花机器人”，通过传感器感知土壤湿度，自动完成浇水任务，兼具趣味性与实用性；中学组的 “社区消毒巡逻机器人”，搭载了智能导航和喷雾系统，可自主规划路线进行无死角消毒，展现出对社会问题的深度思考。选手们在作品演示和答辩环节中，条理清晰地介绍设计理念与技术原理，现场评委也针对作品提出专业建议，让比赛兼具竞技性与学习性。​</p><p>“智创未来” 机器人创意比赛不仅为青少年提供了展示科技才华的平台，更点燃了他们对机器人技术与人工智能的探索热情。赛后，许多参赛学生表示，通过比赛提升了自己的动手能力和问题解决能力，也结识了志同道合的伙伴。此次比赛的成功举办，对推动青少年科技教育发展、培育未来科技创新人才具有深远意义，为科技强国建设注入了青春力量。</p>', 19, 2, '通过', NULL, '2025-04-29 23:11:10');

-- ----------------------------
-- Table structure for notice
-- ----------------------------
DROP TABLE IF EXISTS `notice`;
CREATE TABLE `notice`  (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `title` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '公告标题',
  `content` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL COMMENT '公告内容',
  `time` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '发布时间',
  PRIMARY KEY (`id`) USING BTREE
